+	history.ParentID = history.ID
+	history.ID = ts
+	history.ForkReason = "strip_signatures"
+
+	// Save to same dir with new timestamp
+	dir := filepath.Dir(inputPath)
+	newPath := filepath.Join(dir, fmt.Sprintf("%s_history.json", ts))
